import Card from '../UI/Card';
import styles from './AvailableMeals.module.css'
import MealItem from './MealItem/MealItem';

const DUMMY_MEALS = [
    {
        id: 'm1',
        name: 'Sushi',
        description: 'Finest fish and veggies',
        price: 22.99,
    },
    {
        id: 'm2',
        name: 'Schnitzel',
        description: 'A german specialty!',
        price: 16.5,
    },
    {
        id: 'm3',
        name: 'Barbecue Burger',
        description: 'American, raw, meaty',
        price: 12.99,
    },
    {
        id: 'm4',
        name: 'Green Bowl',
        description: 'Healthy...and green...',
        price: 18.99,
    },
];

const addMealsToDB = async () => {
    const response = await fetch('https://hhhusak-meals-default-rtdb.firebaseio.com/meals.json', {
        method: 'POST',
        body: JSON.stringify(DUMMY_MEALS.map(meal => meal)),

    })
    const data = await response.json();
    console.log(data);
}

const AvailableMeals = () => {
    const mealsList = DUMMY_MEALS.map((meal) => {
        return (
            <MealItem
                key={meal.id}
                id={meal.id}
                name={meal.name}
                description={meal.description}
                price={meal.price}
            />
        )
    });

    return (
        <section className={styles.meals}>
            <Card>
                <ul>{mealsList}</ul>
                <button onClick={addMealsToDB}>ADDDDD DATABASE AHAHAHA</button>
            </Card>
        </section>
    )
}

export default AvailableMeals;
