import { useRef, useState } from 'react';
import styles from './Checkout.module.css';

const isEmpty = (value) => value.toString().trim() === '';
const isNotFiveDigits = (value) => value.toString().trim().length !== 5;

const Checkout = (props) => {
    const [formInputsValidity, setFormInputsValidity] = useState({
        name: true,
        street: true,
        city: true,
        postcode: true
    })

    const nameInputRef = useRef();
    const streetInputRef = useRef();
    const postcodeInputRef = useRef();
    const cityInputRef = useRef();

    const confirmHandler = (event) => {
        event.preventDefault();

        const enteredName = nameInputRef.current;
        const enteredStreet = streetInputRef.current;
        const enteredPostcode = postcodeInputRef.current;
        const enteredCity = cityInputRef.current;

        const enteredNameIsValid = !isEmpty(enteredName);
        const enteredStreetIsValid = !isEmpty(enteredStreet);
        const enteredPostcodeIsValid = !isNotFiveDigits(enteredPostcode);
        const enteredCityIsValid = !isEmpty(enteredCity);

        setFormInputsValidity({
            name: enteredNameIsValid,
            street: enteredStreetIsValid,
            city: enteredCityIsValid,
            postcode: enteredPostcode
        })

        const formIsValid = enteredNameIsValid &&
            enteredStreetIsValid &&
            enteredPostcodeIsValid &&
            enteredCityIsValid;

        if (!formIsValid) {
            return;
        }
    };

    return (
        <form className={styles.form} onSubmit={confirmHandler}>
            <div className={styles.control}>
                <label htmlFor='name'>Your Name</label>
                <input type='text' id='name' ref={nameInputRef} />
                {!formInputsValidity.name && <p className={styles.invalid}>Please enter valid name.</p>}
            </div>
            <div className={styles.control}>
                <label htmlFor='street'>Street</label>
                <input type='text' id='street' ref={streetInputRef} />
                {!formInputsValidity.street && <p className={styles.invalid}>Please enter valid street.</p>}
            </div>
            <div className={styles.control}>
                <label htmlFor='postal'>Postal Code</label>
                <input type='text' id='postal' ref={postcodeInputRef} />
                {!formInputsValidity.postcode && <p className={styles.invalid}>Please enter valid postal code.</p>}
            </div>
            <div className={styles.control}>
                <label htmlFor='city'>City</label>
                <input type='text' id='city' ref={cityInputRef} />
                {!formInputsValidity.city && <p className={styles.invalid}>Please enter valid city.</p>}
            </div>
            <div className={styles.actions}>
                <button type='button' onClick={props.onCancel}>
                    Cancel
                </button>
                <button className={styles.submit} onClick={confirmHandler}>Confirm</button>
            </div>
        </form>
    );
};

export default Checkout;
