import styles from './MealItem.module.css';

const MealItem = props => {
    const price = `$${props.price.toFixed(2)}`;

    return (
        <li className={styles.meal}>
            <div>
                <h3>{props.name}</h3>
            </div>
            <div className={styles.description}>
                {props.description}
            </div>
            <div className={price}>
                {props.price}
            </div>
        </li>
    )
}

export default MealItem;
